import '../index.css';

const TaskInput = (props) => {

    const closeTaskHandler = () => {
        props.closeNewTask();
    };

    const submitHandler = (event) => {
        event.preventDefault();
    }

    return(
        <div className="flex flex-col">
            <form onSubmit={submitHandler} className="form flex flex-row items-center">
                {/* <input type="checkbox" className="w-4 h-4 rounded-full focus:ring-1" /> */}
                <button onClick={closeTaskHandler} type="button" className="bg-white rounded-full items-center justify-center text-gray-500 hover:text-gray-500 hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-gray-500">
                <span className="sr-only">Close menu</span>
                <svg className="h-4 w-4" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" aria-hidden="true">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                </svg>
                </button>
                <div className="ml-3 flex flex-col">
                    <input className="border-0 p-0 text-base font-medium focus:ring-0" placeholder="Enter Task Name" type="text"></input>
                    <input className="text-xs text-gray-500 p-0 border-0 focus:ring-0" placeholder="Task Description" type="text"></input>
                </div>
            </form>
            <div>
                <button className="p-2 border rounded-xl text-xs border-gray-500">Confirm</button>
            </div>
        </div>
    );
};

export default TaskInput;
