import React, {useState, useEffect} from 'react';
import {XIcon} from '@heroicons/react/solid';
import $ from 'jquery';
import { data } from 'autoprefixer';
import FriendsItem from './FriendsItem.js';

const FriendsList = (props) => {

    const [friends, setFriends] = useState('');
    let taskContent;

    //get friends list
    if (friends == ''){
        console.log(props.userId);
        $.get("https://us-central1-task-manager-api-4f9a8.cloudfunctions.net/user/friends/" + props.userId,
        function(data, status){
            const friendList = JSON.parse(data);
            setFriends(friendList);
        });
    }

    useEffect(()=>{
        if (friends.length > 0){
            taskContent = friends.map(data =>
                <FriendsItem username={data.username} id={props.id} />)
        }

    },[friends])

    return(
        <ul className="flex flex-col mb-3">
            {taskContent}
        </ul>
    )
};

export default FriendsList;
