import React, { useState } from 'react'; 

const TaskItem = (props) => {

    const taskCheckHandler = (event) => {
        // enter task id
        // const taskId = 
        props.onTaskChecked(event);
    };

    return (
        <div className="flex flex-row items-center mb-3">
            <input type="checkbox" onChange={taskCheckHandler} className="w-4 h-4 rounded-full focus:ring-1" />
            <div className="ml-3">
                <p className="text-base font-medium">{props.taskName}</p>
                <p className="text-xs text-gray-500">{props.taskDescription}</p>
             </div>
        </div>
    );
}

export default TaskItem;
