import Fade from 'react-reveal/Fade';
import Signup from './Signup.js'
import { getAuth, onAuthStateChanged } from "firebase/auth";

const auth = getAuth();

const Account = (props) => {
    const CloseAccountHandler = () => {
        props.onAccountClose();
    };

    const accountState = [
        <Signup onAccountClose={CloseAccountHandler} />,
        <div>Login</div>,
        <Fade className="flex flex-col m-6">
                            <div className="ml-auto">
                    <button onClick={CloseAccountHandler}
                    className="flex w-8 h-8 m-3 rounded-full focus:ring-2 ml-3 hover:scale-110 bg-white hover:bg-gray-200 duration-300 justify-center items-center">
                        <XIcon className="w-5 h-5"/>
                    </button>
                </div>
            <h1>Welcome to My Task!</h1>
            <p>Login or Sign up for an account to get started!</p>
        </Fade>
    ];

    useEffect(() => { //updates userId state on auth change
        onAuthStateChanged(auth, (user) => { //gets user's uid on sign in or sign up
        if (user) {
            const uid = user.uid;

        } else {

        }
        });
    })

    const [accountDiv, setAccountDiv] = useState(accountState[2]);

    return(
    <div className="w-full sm:w-3/4 lg:w-1/2
    absolute bg-white rounded-lg drop-shadow-xl justify-self-center m-auto left-0 right-0">
        {accountDiv}
    </div>
    )
};

export default Account;
