import React, {useState} from 'react';
import Signup from './Signup.js'
import { getAuth, onAuthStateChanged } from "firebase/auth";

const auth = getAuth();

const Account = (props) => {

  //function ensures userId & username is always up to date with current user
    useEffect(() => { //updates userId state on auth change
        onAuthStateChanged(auth, (user) => { //gets user's uid on sign in or sign up
        if (user) {
            const uid = user.uid;
            if (userId != uid){
            $.get("https://us-central1-task-manager-api-4f9a8.cloudfunctions.net/user/" + uid, function(data, status){
                const userData = JSON.parse(data);
                setUsername(userData.newUserData.username);
                setUserId(uid);
            })
            };
        } else {

        }
        });
    })

    const CloseAccountHandler = () => {
        props.onAccountClose();
    };

    const accountState = [
        <Signup onAccountClose={CloseAccountHandler} />,
        <div>hello</div>
    ];

    const [accountDiv, setAccountDiv] = useState(accountState[0]);

    return(
    <div className="w-full sm:w-3/4 lg:w-1/2
    absolute bg-white rounded-lg drop-shadow-xl justify-self-center m-auto left-0 right-0">
        {accountDiv}
    </div>
    )
};

export default Account;
