import Fade from 'react-reveal/Fade';
import {XIcon} from '@heroicons/react/solid'
import Signup from './Account.js'

const accountState = [
    <div>
        <h1 className="font-medium text-2xl">Sign up</h1>
    </div>,
    <div>
        <h1 className="font-medium text-2xl">Login</h1>
    </div>
]

const Account = (props) => {
    const [accountDiv, setAccountDiv] = useState(accountState[0]);

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const CloseAccountHandler = () => {
        props.onAccountClose();
    }

    const EmailChangeHandler = (email) =>{
        setEmail(email.target.value);
    }

    const PasswordChangeHandler = (password) => {
        setPassword(password.target.value);
    }

    const SignUpHandler = (data) => {
        data.preventDefault();
        const accountData = {
            email: email,
            password: password
        }

        //Create account with database
        if (accountData.email != '' && accountData.password != ''){
            console.log(accountData);
            //create account using firebase rest auth
        }
    }

    return(
    <div className="absolute backdrop-blur-md bg-white rounded-lg drop-shadow-xl w-1/2 justify-self-center m-auto left-0 right-0">
        {}
    </div>
    )
};

export default Account
