import React, {useState} from 'react';
import Fade from 'react-reveal/Fade';

const accountState = [
    <div>
        <h1 className="font-medium text-2xl">Sign up</h1>
    </div>,
    <div>
        <h1 className="font-medium text-2xl">Login</h1>
    </div>
]

const Account = () => {
    const [accountDiv, setAccountDiv] = useState(accountState[0]);

    return(
    <div className="absolute backdrop-blur-md bg-white rounded-lg drop-shadow-xl w-1/2 h-1/2 justify-self-center m-auto left-0 right-0">
        <Fade>
            <div className="flex flex-col m-8">
                <div>
                    <h1 className="font-medium text-2xl">Sign up</h1>
                    <p class="my-4 opacity-70">Sign up to start using My Tasks!</p>
					<hr class="my-6" />
					<label class="uppercase text-sm font-bold opacity-70">Email</label>
					<input type="text" class="p-2 mt-2 mb-4 w-full bg-slate-200 rounded border-2 border-slate-200 focus:border-slate-600 focus:outline-none" />
                </div>
            </div>
        </Fade>
    </div>
    )
};

export default Account
