import React, {useState} from 'react';
import './index.css';
import TaskDisplay from './components/TaskDisplay.js'
import TaskInput from './components/TaskInput.js';

function App() {
  const [newTask, setNewTask] = useState();

  const closeTaskHandler = () => {
    setNewTask();
  }

  const newTaskHandler = () => {
    setNewTask(<TaskInput closeNewTask={closeTaskHandler}/>)
  }

  return (
    <div className="grid grid-cols-1 gap-4 m-5 justify-center max-w-screen-md">
      <div>
        <h1 className="text-3xl font-bold font-sans">My Tasks</h1>
      </div>
      <TaskDisplay ></TaskDisplay>
      {newTask}
      <div className="fixed right-5 bottom-5">
        <button 
        onClick={newTaskHandler} 
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full">+
        </button>
      </div>
    </div>
  );
}

export default App;
