import React, {useState} from 'react';
import Signup from './Signup.js'

const Account = (props) => {
    const CloseAccountHandler = () => {
        props.CloseAccountHandler();
    };

    const accountState = [
        <Signup onAccountClose={CloseAccountHandler} />
        ,
        <div>hello</div>
    ];

    const [accountDiv, setAccountDiv] = useState(accountState[0]);

    return(
    <div className="w-full sm:w-3/4 lg:w-1/2
    absolute backdrop-blur-md bg-white rounded-lg drop-shadow-xl justify-self-center m-auto left-0 right-0">
        {accountDiv}
    </div>
    )
};

export default Account;
